        '''Check this pattern against given `path`.'''
        return bool(self.regex.match(path))

    def _glob_to_regex(self, pattern):
        '''Convert a glob pattern to a regular expression.'''
        i, n = 0, len(pattern)
        res = ''
        while i < n:
            c = pattern[i]
            i += 1
            if c == '*':
                res += '.*'
            elif c == '?':
                res += '.'
            elif c == '[':
                j = i
                if j < n and pattern[j] == '!':
                    j += 1
                if j < n and pattern[j] == ']':
                    j += 1
                while j < n and pattern[j] != ']':
                    j += 1
                if j >= n:
