        :param file_name:
        :type file_name: str
        :returns:
        '''
        kwargs = {}
        with h5py.File(file_name, 'r') as f:
            for field in fields(cls):
                key = field.name
                if key in f.attrs:
                    val = f.attrs[key]
                    # h5py returns bytes for strings
                    if isinstance(val, bytes):
                        val = val.decode()
                    if val == 'None':
                        val = None
                    kwargs[key] = val
