import select

class SocketReceiver:
    '''A receiver for multiple sockets.'''

    def __init__(self):
        '''Set up the receiver.'''
        self._registered_sockets = set()

    def register(self, socket):
        '''Register the socket.'''
        self._registered_sockets.add(socket)

    def unregister(self, socket):
        '''Unregister the socket.'''
        self._registered_sockets.discard(socket)

    def receive(self, *sockets, timeout=None):
        '''Timeout is in seconds.'''
        if sockets:
            readable_sockets = select.select(sockets, [], [], timeout)[0]
        else:
            readable_sockets = select.select(self._registered_sockets, [], [], timeout)[0]
        return readable_sockets
