                ("sLen", c_ulong)
            ]

        param = CK_RSA_PKCS_PSS_PARAMS()
        param.hashAlg = hashAlg
        param.mgf = mgf
        param.sLen = sLen

        param_ptr = ctypes.pointer(param)
        return ctypes.cast(param_ptr, c_void_p)

