        if env is None:
            env = os.environ.copy()
        else:
            env = {**os.environ, **env}

        if cwd is None:
            cwd = self.path

        if echo:
            print(f"Executing: {' '.join(cmd)}")

        try:
            result = run(cmd, env=env, cwd=cwd, capture_output=not quiet, text=True, check=check)
        except CalledProcessError as e:
            if debug:
                print(f"Error executing command: {e}")
            raise

