    service = UserProfileService()
    user_profile = {
        'user_id': '12345',
        'name': 'John Doe',
        'email': 'john@example.com'
    }
    service.save(user_profile)
    print(service.lookup('12345'))  # Output: {'user_id': '12345', 'name': 'John Doe', 'email': 'john@example.com'}
