        :return: The error response for the exception.
        '''
        pass

class ConcreteErrorHandler(ErrorHandler):
    def can_handle(self, e):
        return isinstance(e, Exception)

    def handle(self, e):
        error_response = {
            'error': str(e),
            'type': type(e).__name__
