        density = (keyword_hits + code_like * 2) / total_words
        return min(density * 10, 1.0)  # scale up, cap at 1.0

    def _calculate_concept_complexity(self, content: str) -> float:
        '''Calculate concept complexity'''
        # Use average sentence length and unique technical terms as proxy
        sentences = re.split(r'[.!?]', content)
        sentences = [s for s in sentences if s.strip()]
        avg_sentence_length = sum(len(s.split()) for s in sentences) / max(1, len(sentences))
        # Technical terms: words longer than 8 chars or containing digits/symbols
        words = re.findall(r'\w+', content)
        tech_terms = [w for w in words if len(w) > 8 or re.search(r'\d|_', w)]
        unique_tech_terms = set(tech_terms)
        complexity = (avg_sentence_length / 25.0 + len(unique_tech_terms) / 20.0) / 2
        return min(complexity, 1.0)

    def _calculate_implementation_detail_level(self, content: str) -> float:
        '''Calculate implementation detail level'''
        # Look for implementation-specific words and code snippets
        impl_keywords = [
            'implementation', 'code', 'snippet', 'source', 'class', 'function', 'variable', 'object', 'method',
            'parameter', 'argument', 'return', 'call', 'instantiate', 'constructor', 'destructor', 'inheritance'
        ]
        code_blocks = len(re.findall(r'```[\s\S]*?```', content))
        keyword_hits = sum(len(re.findall(r'\b' + re.escape(kw) + r'\b', content.lower())) for kw in impl_keywords)
        total_words = max(1, len(re.findall(r'\w+', content)))
        detail_level = (keyword_hits + code_blocks * 3) / total_words
