    @staticmethod
    def get_timezone() -> str:
        '''Detect system timezone.'''
        # Try using time.tzname
        if hasattr(time, 'tzname'):
            # On most systems, time.tzname[0] is the local timezone name
            # On Windows, time.tzname[0] is standard, [1] is daylight
            # On Unix, time.tzname[0] is local
            # Try to detect if DST is in effect
            is_dst = time.localtime().tm_isdst
            try:
                tz = time.tzname[is_dst]
            except Exception:
                tz = time.tzname[0]
            if tz:
                return tz

        # Try using environment variable
        tz_env = os.environ.get('TZ')
        if tz_env:
            return tz_env

        # Try using /etc/timezone (Linux)
        if sys.platform.startswith('linux'):
            try:
                with open('/etc/timezone') as f:
                    return f.read().strip()
            except Exception:
                pass

        # Try using /etc/localtime symlink (Linux)
        if sys.platform.startswith('linux'):
            try:
                localtime_path = os.path.realpath('/etc/localtime')
                if '/zoneinfo/' in localtime_path:
                    return localtime_path.split('/zoneinfo/')[-1]
            except Exception:
                pass

        # Fallback
        return 'Unknown'

    @staticmethod
    def get_time_format() -> str:
        '''Detect system time format ('12h' or '24h').'''
        # Try to get locale time format
        try:
            locale.setlocale(locale.LC_TIME, '')
            time_format = locale.nl_langinfo(locale.T_FMT)
            # If %p (AM/PM) is in the format, it's 12h
            if '%p' in time_format or '%I' in time_format:
                return '12h'
            else:
                return '24h'
        except Exception:
            pass

