    def _should_evict_for_memory(self) -> bool:
        """Check if we should evict entries due to memory pressure."""
        try:
            cache_size = sum((sys.getsizeof(v) for v in self.cache.values()))
            return cache_size > self.max_memory_bytes
        except Exception:
