    '''A wheel file'''

    WHEEL_FILENAME_RE = re.compile(
        r"""^(?P<distribution>[^-]+)-
            (?P<version>[^-]+)
            (?:-(?P<build_tag>[^-]+))?
            -(?P<python_tag>[^-]+)
            -(?P<abi_tag>[^-]+)
            -(?P<platform_tag>[^-]+)
            \.whl$
