        self.distribution = m.group('distribution')
        self.version = m.group('version')
        self.build_tag = m.group('build_tag')
        self.python_tag = m.group('python_tag')
        self.abi_tag = m.group('abi_tag')
        self.platform_tag = m.group('platform_tag')

