        py_tags = self.python_tag.split('.')
        abi_tags = self.abi_tag.split('.')
        plat_tags = self.platform_tag.split('.')
        for py in py_tags:
            for abi in abi_tags:
                for plat in plat_tags:
                    self._file_tags.append(Tag(py, abi, plat))

    def get_formatted_file_tags(self) -> List[str]:
        '''Return the wheel's tags as a sorted list of strings.'''
        tags = [str(tag) for tag in self._file_tags]
        return sorted(tags)

    def support_index_min(self, tags: List[Tag]) -> int:
        '''Return the lowest index that one of the wheel's file_tag combinations
        achieves in the given list of supported tags.
        '''
        file_tags_set = set(self._file_tags)
        for idx, tag in enumerate(tags):
            if tag in file_tags_set:
                return idx
        raise ValueError("None of the wheel's file tags match the supported tags.")

    def find_most_preferred_tag(self, tags: List[Tag], tag_to_priority: Dict[Tag, int]) -> int:
        '''Return the priority of the most preferred tag that one of the wheel's file
        tag combinations achieves in the given list of supported tags using the given
        tag_to_priority mapping, where lower priorities are more-preferred.
        '''
        min_priority = None
        for tag in self._file_tags:
            if tag in tag_to_priority:
                priority = tag_to_priority[tag]
                if min_priority is None or priority < min_priority:
                    min_priority = priority
        if min_priority is None:
            raise ValueError("None of the wheel's file tags match the supported tags.")
        return min_priority

    def supported(self, tags: Iterable[Tag]) -> bool:
        '''Return whether the wheel is compatible with one of the given tags.'''
        file_tags_set = set(self._file_tags)
        for tag in tags:
            if tag in file_tags_set:
                return True
        return False
