class CodeManager:
    '''
    Manages code content extraction, storage, and formatting.
    This class provides robust methods for extracting Python code from LLM responses
    using multiple fallback strategies, similar to HumanEval evaluator approaches.
    '''

    def __init__(self):
        '''Initialize code storage.'''
        self.codes: Dict[str, str] = {}  # filename -> code
        self.default_filename = "main.py"

    def update_from_response(self, response: str) -> None:
        '''
        Update codes from LLM response using robust extraction methods.
        Args:
            response: Raw LLM response text containing code
        '''
        code_map = self._extract_code_with_fallbacks(response)
        if isinstance(code_map, dict):
            self.codes = code_map
        elif isinstance(code_map, str):
            self.codes = {self.default_filename: code_map}
        else:
            self.codes = {}

    def _extract_code_with_fallbacks(self, text: str):
        '''
        Extract Python code from text using multiple fallback methods.
        This method tries several extraction strategies in order of preference:
        1. Look for "## Validated Code" section (from other agents)
        2. Extract from ```python``` fenced blocks
        3. Find function definition patterns
        4. Parse filename + code patterns  
        5. Last resort: find any Python-like syntax
        Args:
            text: Input text containing code
        Returns:
            Extracted code string, or empty string if no code found
        '''
        # 1. Look for "## Validated Code" section
        validated_match = re.search(r"##\s*Validated Code\s*\n+((?:.|\n)+?)(?:\n##|\Z)", text, re.IGNORECASE)
        if validated_match:
            code = validated_match.group(1).strip()
            if code:
                return {self.default_filename: code}

        # 2. Extract from ```python fenced blocks
        code_blocks = re.findall(r"```(?:python)?\s*\n(.*?)(?:(?:\n)?```)", text, re.DOTALL | re.IGNORECASE)
        if code_blocks:
            # Try to detect filename headers before code blocks
            code_map = {}
            for block in code_blocks:
                # Look for preceding filename
                filename_match = re.search(r"(?:^|\n)([^\s:]+\.py)\s*:?[\r\n]+$", text[:text.find(block)], re.MULTILINE)
                filename = filename_match.group(1).strip() if filename_match else None
                if not filename:
                    filename = self.default_filename if not code_map else f"file{len(code_map)+1}.py"
                code_map[filename] = block.strip()
            return code_map

        # 3. Find function definition patterns
        func_defs = re.findall(r"^(def\s+\w+\s*\(.*?\):(?:\n(?: {4}|\t).*)*)", text, re.MULTILINE)
        if func_defs:
            code = "\n\n".join(func_defs)
            return {self.default_filename: code}

        # 4. Parse filename + code patterns (e.g., "foo.py\n<code>")
        file_code_pattern = re.findall(
            r"([^\s:]+\.py)\s*:?[\r\n]+((?:.|\n)*?)(?=(?:[^\s:]+\.py\s*:?\n|$))",
            text, re.MULTILINE)
        if file_code_pattern:
            code_map = {}
            for filename, code in file_code_pattern:
                code = code.strip()
                if code:
                    # Remove trailing triple backticks if present
                    code = re.sub(r"```$", "", code).strip()
                    code_map[filename.strip()] = code
            if code_map:
                return code_map

        # 5. Last resort: find any Python-like syntax
        code = self._extract_python_like_content(text)
        if code:
            return {self.default_filename: code}

        return {self.default_filename: ""}

    def _extract_python_like_content(self, text: str) -> str:
        '''
        Last resort extraction method for Python-like content.
        Args:
            text: Input text
        Returns:
            Extracted Python-like code or empty string
        '''
        # Try to find at least one function or class definition
        matches = re.findall(r"^(def|class)\s+\w+\s*\(?.*?\)?:.*(?:\n(?: {4}|\t).*)*", text, re.MULTILINE)
        if matches:
            return "\n\n".join(matches)
        # Otherwise, try to find lines that look like Python code (heuristic)
        lines = text.splitlines()
        code_lines = [line for line in lines if re.match(r"^\s*(def |class |import |from |if |for |while |return |print\()", line)]
        if code_lines:
            return "\n".join(code_lines)
        return ""

    def get_formatted_codes(self) -> str:
        '''
        Get formatted codes for display purposes.
        Returns:
            Formatted string with filename and code blocks
        '''
        if not self.codes:
            return ""
        result = []
        for filename, code in self.codes.items():
            result.append(f"{filename}\n```python\n{code.strip()}\n```")
        return "\n\n".join(result)

    def get_raw_code(self) -> str:
        '''
        Get raw code content without formatting.
        Returns:
            Raw code string, concatenated if multiple files exist
        '''
        if not self.codes:
            return ""
        return "\n\n".join(code.strip() for code in self.codes.values())

    def has_code(self) -> bool:
        '''Check if any code has been stored.'''
        return bool(self.codes and any(code.strip() for code in self.codes.values()))
