        capabilities = {}

        if os_type == "Linux":
            capabilities["scheduler"] = shutil.which("crontab") is not None
            capabilities["service_manager"] = shutil.which("systemctl") is not None
        elif os_type == "Windows":
            capabilities["scheduler"] = shutil.which("schtasks") is not None
            capabilities["service_manager"] = shutil.which("sc.exe") is not None
        elif os_type == "Darwin":
            # macOS: crontab is available, but no systemctl/sc.exe
            capabilities["scheduler"] = shutil.which("crontab") is not None
            capabilities["service_manager"] = False
        else:
            capabilities["scheduler"] = False
            capabilities["service_manager"] = False

        self.capabilities = capabilities
        return capabilities

    def _log_capability_warnings(self) -> None:
        '''
        Internal helper to log warnings if essential system capabilities are missing.
        '''
        logger = getattr(self, "logger", None)
        if logger is None:
            logger = logging.getLogger(__name__)

        caps = getattr(self, "capabilities", {})
        if not caps:
            return

        if not caps.get("scheduler", False):
            logger.warning("System task scheduler (crontab or schtasks) not found. Scheduled tasks will be unavailable.")
