        atom_indices = {}
        atom_indices['reactants'] = []
        atom_indices['products'] = []
        sides = in_smiles.split('>')
        if len(sides) < 3:
            raise ValueError(f"The following is not a valid reaction SMILES: '{in_smiles}'")
        if len(sides[1]) > 0:
            sides[0] += '.' + sides[1]
