        hash_values = []
        for t in shingling:
            h = int(blake2b(t, digest_size=4).hexdigest(), 16)
            h = h & 4294967295
            if h >= 2147483648:
                h -= 4294967296
            hash_values.append(h)
        return np.array(hash_values, dtype=np.int32)

