    @staticmethod
    def is_local_path(path: str) -> bool:
        """判断是否为本地路径"""
        path = path.strip('"\'')
        if re.match('^https?://', path, re.IGNORECASE) or re.match('^ftp://', path, re.IGNORECASE):
            return False
