                    if match.startswith('www.'):
                        match = 'https://' + match
                    else:
                        match = 'https://' + match
                url = cls.convert_arxiv_url(match.rstrip('/'))
                urls.append(url)
        seen = set()
        unique_urls = []
        for url in urls:
            if url not in seen:
                seen.add(url)
                unique_urls.append(url)
        return unique_urls

    @staticmethod
    def infer_filename_from_url(url: str) -> str:
        """从URL推断文件名"""
