    artifacts: Dict[str, Union[str, bytes]] = field(default_factory=dict)

    @classmethod
    def from_dict(cls, metrics: Dict[str, float]) -> 'EvaluationResult':
        """Auto-wrap dict returns for backward compatibility"""
        return cls(metrics=metrics)

