        if self._file_path is not None:
            ext = self._file_path.suffix.lower()
            if ext in {'.py'}:
                return 'python'
            if ext in {'.yaml', '.yml'}:
                return 'yaml'
        raise RuntimeError("Unable to determine agent kind")

