    def extract_local_paths(text: str) -> List[str]:
        """从文本中提取本地文件路径"""
        patterns = ['"([^"]+)"', "'([^']+)'", '(?:^|\\s)((?:[~./\\\\]|[A-Za-z]:)?(?:[^/\\\\\\s]+[/\\\\])*[^/\\\\\\s]+\\.[A-Za-z0-9]+)(?:\\s|$)', '(?:^|\\s)((?:~|\\.{1,2})?/[^\\s]+)(?:\\s|$)', '(?:^|\\s)([A-Za-z]:[/\\\\][^\\s]+)(?:\\s|$)', '(?:^|\\s)(\\.{1,2}[/\\\\][^\\s]+)(?:\\s|$)']
        local_paths = []
        potential_paths = []
        for pattern in patterns:
