        for line in lines:
            line_stripped = line.strip()
            if re.match('^\\|\\s*[-:]+\\s*(\\|\\s*[-:]+\\s*)+\\|?$', line_stripped):
                continue
            columns = [col.strip() for col in line_stripped.split('|') if col.strip() != '']
