    def analyze_bottlenecks(self, report: dict[str, float]) -> list[str]:
        if not report:
            return []
        durations = list(report.values())
        total = sum(durations)
        if total <= 0:
            return []
        if len(durations) == 1:
            name, value = next(iter(report.items()))
            return [name] if value >= 0.1 else []

