            except Exception:
                pass
        elif system == 'Linux':
            if os.path.exists('/etc/timezone'):
                try:
                    with open('/etc/timezone') as f:
                        tz_content: str = f.read().strip()
                        if tz_content:
                            return tz_content
                except Exception:
