                if tz_result:
                    return tz_result
            except Exception:
                pass
        elif system == 'Windows':
            with contextlib.suppress(Exception):
                tzutil_result: subprocess.CompletedProcess[str] = subprocess.run(['tzutil', '/g'], capture_output=True, text=True, check=True)
