    OPERATORS = ('&&', '||', ';', '|', '>', '<', '>>', '<<', '$(', '`')

    @staticmethod
    def is_shell_control_start(line: str) -> bool:
        """Check if a line starts a shell control structure."""
        stripped = line.lstrip('@-+ ')
        control_patterns = ['^if\\s+\\[', '^for\\s+', '^while\\s+', '^case\\s+', '^until\\s+', '^{\\s*$']
